-- ----------------------------
-- Table structure for ac_account
-- ----------------------------
DROP TABLE IF EXISTS `ac_account`;
CREATE TABLE `ac_account`  (
  `CODE` varchar(15) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '账户编号',
  `OWN_CODE` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '所属实体用户编号',
  `ACC_TYPE` varchar(2) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '账户类型：安全控制值\r\n01:电子钱包(零钱账号)：即电商的内部账号，用户、商户、清算单位需要建立零钱账户\r\n02:第三方支付账号：用户在第三方支付机构建立的账户。\r\n03:银行卡账号：用户的银行卡信息，每个卡对应一个账户。\r\n04:结算账号：用来支持和第三方支付公司、银行进行结算用。\r\n05:现金账户\r\n06:运营商柜台总账户\r\n07:运营商虚拟账户',
  `ACC_NAME` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '账户名称',
  `ACC_CURRENCY` varchar(3) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '货币类型',
  `CONTROL_FLAG` int(11) NOT NULL COMMENT '账户控制相关：\r\n•是否允许充值；\r\n•是否允许提现；\r\n•是否允许透支；\r\n•是否允许支付；\r\n•是否允许转账进入；\r\n•是否允许转账转出；\r\n•是否有安全保障；\r\n•是否激活；\r\n•是否冻结;\r\n',
  `BANK_DETAIL` varchar(256) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '绑定银行卡的资料（需要加密处理），以JSON方式保存，包括：\r\ntype：银行卡类型，包含Credit or Debit\r\ncode：银行编码\r\nbank：银行名称\r\nname ：账户名称\r\naccount : 账号\r\n',
  `AMT` decimal(20, 2) NOT NULL COMMENT '总金额',
  `AMT_CASH` decimal(20, 2) NOT NULL COMMENT '可提现金额：安全控制值',
  `AMT_UNCASH` decimal(20, 2) NOT NULL COMMENT '不可提现金额：安全控制值',
  `AMT_FREEZE` decimal(20, 2) NOT NULL COMMENT '冻结金额：安全控制值\r\n冻结余额指在账户上暂不能使用的额度。在支付的时候，往往是先冻结，商品出库后， 再实际执行扣款。',
  `TAX_PERCENT` decimal(20, 2) NOT NULL DEFAULT 0.00 COMMENT '税金支付百分比(存百分比整数)',
  `STATUS` varchar(2) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '账户状态：\r\n00：未生效\r\n01：生效\r\n02：冻结\r\n03：注销',
  `CHECKSUM` varchar(128) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '账户验证码',
  `TIME_CREATE` datetime NOT NULL COMMENT '账户建立时间',
  `TIME_UPDATE` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '账户更新时间',
  PRIMARY KEY (`CODE`) USING BTREE,
  INDEX `idx_own_code`(`OWN_CODE`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '账户定义表' ROW_FORMAT = COMPACT;

-- ----------------------------
-- Records of ac_account
-- ----------------------------

