  `SCURITY_LEVEL` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '用户的安全等级，根据业务需要来设置。',
  `STATUS` varchar(2) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '用户状态：\r\n01：未审核\r\n02：已审核（正常使用）\r\n03：冻结\r\n04：销户',
  `REMARKS` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '预留信息',
  PRIMARY KEY (`CODE`) USING BTREE,
  INDEX `ix_DEPT_CODE`(`DEPT_CODE`) USING BTREE,
  INDEX `idx_branch_code`(`BRANCH_CODE`) USING BTREE,
  INDEX `idx_user_code`(`USER_CODE`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '实体用户信息表' ROW_FORMAT = COMPACT;

