  `DEVICE_CODE` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '设备编号\r\n如果时移动端，则为IMEI，否则为MAC地址',
  `APPLY_USER` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '申请操作员',
  `APPLY_TIME` datetime NOT NULL COMMENT '业务申请日期',
  `APPLY_REASON` varchar(2) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '申请原因',
  `APPLY_REMARK` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '申请备注',
  `CHECK_USER` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '审核人员',
  `CHECK_TIME` datetime NULL DEFAULT NULL COMMENT '审核时间',
  `CHECK_REMARK` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '审核备注',
