-- ----------------------------
-- Table structure for et_user_service
-- ----------------------------
DROP TABLE IF EXISTS `et_user_service`;
CREATE TABLE `et_user_service`  (
  `CODE` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '服务流水号',
  `MODE` varchar(2) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '服务模式：\r\n01：公有的，PUBLIC\r\n02：私有的，PRIVATE',
  `REF_CODE` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '当服务模式为PUBLIC，此编号被定为实体类型，以适用于所有用户的服务类型：\r\n00运营商（一个运营商仅一个实体用户，用于管理账户，不可销售）\r\n01运营商柜台（一个运营商可以有多个，可销售）\r\n02代理商（一个代理商仅一个实体用户，用于管理账户，不可销售）\r\n03代理商柜台（一个运营商可以有多个，可销售）\r\n04商户（企业）\r\n05直接客户（个人）\r\n当服务模式为PRIVATE，此编号被定为实体用户编号',
  `SVC_CODE` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '服务合同号',
  `SVC_TYPE` varchar(3) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '服务类型，引用SYS_DICTIONARY的SVC_TYPE',
  `SVC_PARAMS` varchar(512) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '服务参数',
  `START_TIME` datetime NOT NULL COMMENT '服务开始时间',
  `EDN_TIME` datetime NOT NULL COMMENT '服务结束时间',
  `USED` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'Y' COMMENT '是否当前使用',
  `ACTIVED` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'Y' COMMENT '是否启用',
  `COPERATOR` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '操作员',
  `CDATE` date NOT NULL COMMENT '建立日期',
  `LAST_TIME` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '变动时间',
  PRIMARY KEY (`CODE`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '实体用户支持服务表' ROW_FORMAT = COMPACT;

