-- ----------------------------
-- Table structure for et_user_session
-- ----------------------------
DROP TABLE IF EXISTS `et_user_session`;
CREATE TABLE `et_user_session`  (
  `CODE` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '实体用户编号',
  `SESSION_NO` int(11) NOT NULL COMMENT '会话编号',
  `SESSION_USER` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '会话操作员编号',
  `SESSION_DATE` datetime NOT NULL COMMENT '事务时间',
  `START_TIME` datetime NOT NULL COMMENT '开柜时间',
  `END_TIME` datetime NULL DEFAULT NULL COMMENT '停柜时间',
  `END_USER` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '停柜操作员',
  `CLOSE_TIME` datetime NULL DEFAULT NULL COMMENT '关柜时间',
  `CLOSE_USER` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '关柜的操作员编号',
  `STATUS` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '标识当前SESSION的状态：\nS：已开始\nP：已暂停\nE：已结束\nC：已关闭',
  `TRANS_COUNT` int(11) NOT NULL COMMENT '事务数据',
  `TRANS_AMT` decimal(15, 2) NOT NULL COMMENT '事务金额',
  `ACC_CODE` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '实体账户（现金账户）',
  `ACC_AMT` decimal(15, 2) NOT NULL COMMENT '实体账户总金额',
  `CASH_SUMMARY` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '实体账户现金账户明细\n现金｜支票\n1，10；5，9｜支票编号，1000；支票编号，2000',
  `CASH_AMT` decimal(15, 2) NULL DEFAULT NULL COMMENT '接收总金额',
  `COLLECT_CODE` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '业务流水号',
  `RECHARGE_CODE` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '现金存入银行的业务流水号',
  `CLOSE_AMT` decimal(15, 2) NULL DEFAULT NULL COMMENT '关柜后的总金额',
  `ADJUST_AMT` decimal(15, 2) NULL DEFAULT NULL COMMENT '调整的金额',
  `ADJUST_TIME` datetime NULL DEFAULT NULL COMMENT '调整的时间',
  `ADJUST_USER` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '调整的用户',
  `ADJUST_NOTES` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '调整的说明',
  `CHECKSUM` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '验证码',
  `LAST_TIME` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '变动时间',
  PRIMARY KEY (`CODE`, `SESSION_NO`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '实体用户会话表' ROW_FORMAT = COMPACT;

