  `OP_SECURITY_CODE` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '0' COMMENT '安全码',
  `STATUS` varchar(2) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '0' COMMENT '操作状态：\r\n01：未生效\r\n02：待审核\r\n03：已审核（生效）\r\n04：已拒绝',
  `CHECKED_TIME` datetime NULL DEFAULT NULL COMMENT '审核或拒绝时间',
  `CHECKED_USER` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '审核或拒绝人员',
  `CHECKED_TEXT` varchar(256) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '审核或拒绝内容',
  `CHECKSUM` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '校验码',
  `LAST_TIME` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '变动时间',
  PRIMARY KEY (`CODE`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '财务人员从柜台收集现金记录表' ROW_FORMAT = COMPACT;

