-- Table structure for fa_jade_data
-- ----------------------------
DROP TABLE IF EXISTS `fa_jade_data`;
CREATE TABLE `fa_jade_data`  (
  `CODE` varchar(40) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '任务编号:\r\n任务编号(3位) +任务类型（3位）+ 时间戳(11位) + 随机数（3位）\r\n',
  `BILL_CODE` varchar(25) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '账单编号',
  `BILL_TIMES` tinyint(4) NULL DEFAULT NULL COMMENT '账单次数',
  `FACT_TYPE` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '账单类型',
  `USER_CODE` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '操作员',
  `TYPABON` varchar(2) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '客户类型',
  `POSABON` varchar(2) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '是否激活用户   4:未激活',
  `CODEXP` varchar(3) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '1' COMMENT '区域编号',
  `BIZ_DATE` date NULL DEFAULT NULL COMMENT '业务日期',
  `BIZ_TYPE` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '业务类型:\r\n1账单;2欠款;3售电\r\n,4充值',
  `PAY_MODE` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '支付模式：1一次全部支付；2部分多次支付',
  `BIZ_CODE` int(50) NULL DEFAULT NULL COMMENT '当天业务序号',
  `TOTAL_AMT` decimal(15, 2) NULL DEFAULT 0.00 COMMENT '账单总金额',
  `AMT` decimal(15, 2) NULL DEFAULT 0.00 COMMENT '实际账单金额或购电金额或欠款金额',
  `RTI` decimal(15, 2) NULL DEFAULT 0.00 COMMENT 'RTI',
  `RER` decimal(15, 2) NULL DEFAULT 0.00 COMMENT 'RER',
  `TREOM` decimal(15, 2) NULL DEFAULT 0.00 COMMENT 'TREOM',
  `PRIMEFIXE` decimal(15, 2) NULL DEFAULT 0.00 COMMENT 'PRIMEFIXE',
  `TVA` decimal(15, 2) NULL DEFAULT 0.00 COMMENT 'TVA',
  `STAMP_TAX` decimal(15, 2) NULL DEFAULT 0.00 COMMENT '印花税',
  `FEE1` decimal(15, 2) NULL DEFAULT NULL COMMENT 'FEE1',
  `FEE2` decimal(15, 2) NULL DEFAULT NULL COMMENT 'FEE2',
  `FEE3` decimal(15, 2) NULL DEFAULT NULL COMMENT 'FEE3',
  `FEE4` decimal(15, 2) NULL DEFAULT NULL COMMENT 'FEE4',
  `FEE5` decimal(15, 2) NULL DEFAULT NULL COMMENT 'FEE5',
  `COPERATOR` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `CDATE` datetime NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`CODE`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '财务数据明细表' ROW_FORMAT = COMPACT;

