DROP TABLE IF EXISTS `fa_jade_item`;
CREATE TABLE `fa_jade_item`  (
  `CODE` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '11位UTC编码+4位随机数',
  `MODE` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '1' COMMENT '模式, sys_dictionary中TableName=FA_JADE_MODE的Code列值',
  `BZ_DATE` date NOT NULL COMMENT '业务日期',
  `AREA_CODE` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '区域编码',
  `FILE_NAME` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '文件名称',
  `FILE_TYPE` varchar(2) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'SR' COMMENT '文件类型',
  `FILE_EXT` varchar(3) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'E10' COMMENT '文件扩展名，\r\nCDN01采用E10\r\n',
  `EXPORT_TIME` datetime NOT NULL COMMENT '生成文件时间',
  `STATUS` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'Y' COMMENT '执行状态\r\nN：未执行\r\nY：已执行\r\n',
  `IS_DOWN` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'N' COMMENT '是否新的导出（未下载过为N）',
  `IS_EMAIL` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'N' COMMENT '是否已发送邮件',
