-- ----------------------------
-- Table structure for fa_jade_out
-- ----------------------------
DROP TABLE IF EXISTS `fa_jade_out`;
CREATE TABLE `fa_jade_out`  (
  `CODE` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '财务文件编号',
  `ROWS` int(11) NOT NULL COMMENT '行数编号',
  `MODE` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '1' COMMENT '模式, sys_dictionary中TableName=FA_JADE_MODE的Code列值',
  `JADE_DATE` date NOT NULL COMMENT '日期',
  `CENTER_GEST` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '管理类型',
  `CODEXP` varchar(3) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '区域',
  `TYPABON` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '用户类型',
  `COD_TRANSACT` varchar(2) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'PT' COMMENT '交易号，固定为PT',
  `NUM_DETAIL` varchar(4) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '用户类型',
  `ID_TRANSACTION1` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '1' COMMENT '交易ID,固定为1',
  `CODE_SENS` varchar(2) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '方向编码\r\n10 = Débit positif\r\n11 = Débit négatif\r\n60 = Crédit positif\r\n61 = Crédit négatif\r\nVoir fichier joint en annexe\r\n财务输出方向：根据业务参照《COMPTA  SMART RELEVE  MRM.XLSX》中对应相关数据\r\n',
  `ENTITE` varchar(4) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'CIEG' COMMENT '财务主体,固定为CIEG',
  `COMPTE` varchar(18) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '财务账户\r\n6位财务科目+12位空，根据业务参照《COMPTA  SMART RELEVE  MRM.XLSX》中对应相关数据\r\n',
  `CENTRE` varchar(12) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '价格中心\r\n3位客户区域编号+9位空格\r\n',
  `C_JOURNAL` varchar(2) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '源代码:天\r\n根据业务参照《COMPTA  SMART RELEVE  MRM.XLSX》中对应相关数据的JNL\r\n',
  `C_MOIS` varchar(2) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '源代码:月\r\n生成文件月份\r\n',
  `C_CODE_EXPLOITATION` varchar(3) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '源代码:运营编码\r\n3位客户地区编号\r\n',
  `C_CODE_CAISSE` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'R' COMMENT '源代码:收银台代码\r\n售电终端序号\r\n',
  `C_JOUR` varchar(2) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '源代码:生产文件的天数',
  `DATE_EFFECTIVE` varchar(8) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '生效日期\r\n月月日日年年年年\r\nMMJJAAAA\r\n售电的时间\r\n',
  `MONTANT` varchar(13) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '移动的金额\r\n13 avec 2 décimales : 9(11)v99\r\n总金额，格式如下\r\n0000003000.00\r\n',
  `ID_TRANSACTION2` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '2' COMMENT '交易ID2\r\n固定值为2\r\n',
  `ZONE_MONETAIRE` varchar(2) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '13' COMMENT '货币地区\r\n固定值为13\r\n',
  `D1_ZONE_MONETAIRE` varchar(2) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '描述-1:货币地区,\r\n固定值为13\r\n',
  `D1_CODE_CAISSE` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '描述1，收银台代码,可为空',
  `D1_JOURNEE_DE_CAISSE` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '1' COMMENT '描述-1,出纳日期\r\n默认值1\r\n',
  `D1_NATURE_OPERATION` varchar(3) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '描述1，操作类型\r\n根据业务参照《COMPTA  SMART RELEVE  MRM.XLSX中对应相关数据的Nature d’op 值为La nature est : OF5 or OF1\r\n',
  `D1_NOMBRE_DE_KWH` varchar(13) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '描述1：KWH数量\r\n此次售电电量，格式如下：\r\n17.8000000000\r\n',
  `D1_TYPE_FACTURE` varchar(2) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '00' COMMENT '描述1-发票类型\r\n默认值00\r\n',
  `DESCRIPTION2` varchar(12) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '描述2，\r\nCRITERE DE LETTRAGE\r\n标出字母的标准\r\n12位空格\r\n',
  `DESCRIPTION3` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '描述3,激活\r\n根据业务参照《COMPTA  SMART RELEVE  MRM.XLSX中对应Activité业务类型不够左补空格填满。有值的话就是14\r\n',
  `CODE_PROJET` varchar(12) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'SMART-RELV' COMMENT '项目代码  \r\n项目编号，为固定值SMART-RELV\r\n',
  `ID_NTRANSACTION3` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '3' COMMENT '交易ID3\r\n交易代码,固定值为3\r\n',
  `SENS_MOUVEMENT` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '1' COMMENT '移动方向（正或是负）\r\n1 = montant positif\r\n2 = montant négatif\r\n方向：1为正，2 为负，决定金额、电量\r\n',
  `IDENTIFICATION_DE_LA_CAISSE` varchar(15) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '银台的识别,\r\n区域编号+R前面补齐15位空格\r\n',
  `NUM_FICHIER` varchar(12) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT 'NUM FICHIER 文件名',
  `ESPACE` varchar(12) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '空白，留12位空格',
  `CDATE` datetime NULL DEFAULT NULL,
  PRIMARY KEY (`CODE`, `ROWS`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '财务文件内容(每行)明细表' ROW_FORMAT = COMPACT;

