-- ----------------------------
-- Table structure for pd_ppe_trans
-- ----------------------------
DROP TABLE IF EXISTS `pd_ppe_trans`;
CREATE TABLE `pd_ppe_trans`  (
  `CODE` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '交易编号',
  `BZ_TIME` datetime NOT NULL COMMENT '交易时间',
  `BRANCH_CODE` varchar(5) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '分支机构',
  `SVR_CODE` varchar(8) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '销售接口编号，来自PD_PPE_SVR',
  `TRANS_TYPE` varchar(2) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '事务类型：\r\n01：购电\r\n02：支付欠款',
  `TRANS_CODE` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '事务编号（由对应接入生成）',
  `TRANS_DATE` datetime NOT NULL COMMENT '事务时间（由服务接口生成）',
  `TRANS_ID` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '事务用户编号（来自销售接口）',
  `TRANS_REF` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '0.00' COMMENT '事务用户合同号（来自销售接口）',
  `TRANS_DEV` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '0.00' COMMENT '事务设备编号（来自销售接口）',
  `TRANS_TIME` int(11) NOT NULL COMMENT '交易次数（来自销售接口）',
  `TRANS_DETAILS` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '事务详情（来自销售接口）',
  `AMT` decimal(15, 2) NOT NULL DEFAULT 0.00 COMMENT '支付总金额（不含印花税）',
  `TAX` decimal(15, 2) NOT NULL COMMENT '支付印花税',
  `NET_AMT` decimal(15, 2) NOT NULL COMMENT '支付总金额（含印花税）',
  `COMM_AMT` decimal(15, 2) NOT NULL COMMENT '支付佣金',
  `PAY_TYPE` varchar(2) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '支付方式：适用于柜台\r\n01：现金\r\n02：支票',
  `PAY_BANK_CODE` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '支票支付号码：适用于柜台',
  `PAY_MODE` varchar(2) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '支付模式：\r\n01：零钱\r\n02：运营商柜台\r\n03：代理商柜台',
  `USER_CODE` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '转出金额的实体用户编号',
  `TAX_ACOUNT` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '印花税收款账户',
  `COMM_ACCOUNT` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '拥金支付账户',
  `STATUS` varchar(2) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '操作状态：\r\n01：未生效\r\n02：待审核\r\n03：已审核\r\n04：正在退款\r\n05：已退款 ',
  `PROJECT_TYPE` char(2) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '01' COMMENT '欠款用户类型： 01 预付费  02 后付费',
  `CHECKED_TIME` datetime NULL DEFAULT NULL COMMENT '审核或退款时间',
  `CHECKED_USER` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '审核或退款人员',
  `CHECKED_TEXT` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '审核或退款内容',
  `REMARK` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '备注',
  `CHECKSUM` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '校验码',
  `LAST_TIME` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '变动时间',
  PRIMARY KEY (`CODE`) USING BTREE,
  INDEX `idx_tid`(`TRANS_ID`) USING BTREE,
  INDEX `idx_dev`(`TRANS_REF`, `TRANS_DEV`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '预付费电表交易记录表' ROW_FORMAT = COMPACT;

