  `AMT` decimal(10, 0) NOT NULL DEFAULT 0 COMMENT '面值',
  `VerifyCode` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '状态验证码',
  `ImportCode` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '导入编号',
  `RefType` varchar(5) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '业务类型（充值0001，空值表示未使用）ECARD_USE_TYPE',
  `RefDate` datetime NULL DEFAULT NULL COMMENT '业务日期',
