  `AgencyCode` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '所属者',
  `UseCode` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '使用者et_user的Code',
  `COperator` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '操作员',
  `CDate` datetime NOT NULL COMMENT '日期',
  PRIMARY KEY (`Status`, `CardID`) USING BTREE,
  UNIQUE INDEX `ix_card_token`(`Status`, `CardTOKEN`) USING BTREE,
  INDEX `ix_card_id`(`CardID`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '刮刮卡信息表(未发行，已发行未使用)' ROW_FORMAT = COMPACT PARTITION BY RANGE (Status)
PARTITIONS 4
(PARTITION `p0` VALUES LESS THAN (1) ENGINE = InnoDB MAX_ROWS = 0 MIN_ROWS = 0 ,
PARTITION `p1` VALUES LESS THAN (2) ENGINE = InnoDB MAX_ROWS = 0 MIN_ROWS = 0 ,
PARTITION `p2` VALUES LESS THAN (3) ENGINE = InnoDB MAX_ROWS = 0 MIN_ROWS = 0 ,
PARTITION `p3` VALUES LESS THAN (MAXVALUE) ENGINE = InnoDB MAX_ROWS = 0 MIN_ROWS = 0 )
;

