-- ----------------------------
-- Table structure for sys_country
-- ----------------------------
DROP TABLE IF EXISTS `sys_country`;
CREATE TABLE `sys_country`  (
  `ID_COUNTRY` int(10) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '国家编码',
  `ID_ZONE` int(10) UNSIGNED NOT NULL COMMENT '区域编码',
