-- Records of sys_dictionary
-- ----------------------------

-- ----------------------------
-- Table structure for sys_event
-- ----------------------------
DROP TABLE IF EXISTS `sys_event`;
CREATE TABLE `sys_event`  (
  `ID` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '操作员所在BranchCode+yymmddhhnnss+3位随机数',
  `USER_CODE` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '操作员编号',
  `EVENT_TIME` datetime NULL DEFAULT NULL COMMENT '操作时间',
  `IP` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT 'IP地址',
  `AREA_NAME` varchar(512) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '所在城市',
  `TYPE` int(4) NULL DEFAULT NULL COMMENT '操作类型',
  `REMARKS` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '备注',
  PRIMARY KEY (`ID`) USING BTREE,
  INDEX `ix_evt_user`(`USER_CODE`, `EVENT_TIME`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '系统事件记录表' ROW_FORMAT = COMPACT;

-- ----------------------------
-- Records of sys_event
-- ----------------------------

-- ----------------------------
-- Table structure for sys_event_rules
-- ----------------------------
DROP TABLE IF EXISTS `sys_event_rules`;
CREATE TABLE `sys_event_rules`  (
  `CODE` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '主键,branchCode+长度为5的顺序编号',
  `BRANCH_CODE` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '所属Branch',
  `DEPT_CODE` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '所属部门',
  `MSG_TYPE` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '消息类型列表，用逗号隔开,如：\r\n1,2,3,4,5,6,\r\n',
  `NOTICE_USER` varchar(1024) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '接收者列表(操作员),如：\r\nKenny,HU',
  `NOTICE_SMS` varchar(1024) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '接收者列表(短信),如:\r\n1304954612,15001254123',
  `NOTICE_EMAIL` varchar(1024) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '接收者列表(邮箱)，如:\r\n123456@qq.com,13456@163.com',
  `ACTIVED` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT 'Y' COMMENT '是否激活',
  `COPERATOR` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '操作员',
  `CDATE` datetime NOT NULL COMMENT '操作时间',
  PRIMARY KEY (`CODE`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '系统事件规则表' ROW_FORMAT = COMPACT;

-- ----------------------------
-- Records of sys_event_rules
-- ----------------------------

-- ----------------------------
-- Table structure for sys_event_type
-- ----------------------------
DROP TABLE IF EXISTS `sys_event_type`;
CREATE TABLE `sys_event_type`  (
  `CODE` int(11) NOT NULL COMMENT '消息编号，按顺序编号。',
  `REF_CODE` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '关联的事件编号，与CODE对应；表示需要处理的关联事件。\r\n如：A相失压结束事件需要处理A相失压开始事件。即：\r\nCODE=202时，REF_CODE=201\r\n',
  `DESCRIPTION` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '消息描述',
  `TYPE` varchar(2) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '消息类型:[00-99]表示\r\n01: 事件上报\r\n02: 任务消息\r\n03：系统消息\r\n04：内部邮件\r\n',
  `PROCESS_TYPE` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '处理类型：\r\n0：不处理\r\n1：自动处理\r\n2：手动处理\r\n',
  `MODE` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '通知方式[0-9],Web通过该字段定义不同的通知方式\r\n1,提醒(通知)\r\n2,警告\r\n3,错误\r\n',
  `LEVEL` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '5' COMMENT '消息优先级[0-9] ,数字越大优先级越高\r\n…\r\n3: 低(L)\r\n4: 低于标准(B)\r\n5: 普通(N)\r\n6：高于标准(A)\r\n7: 高(H)\r\n8: 实时(R)\r\n…\r\n',
  `SEND_TYPE` varchar(3) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '100' COMMENT '默认发送类型{界面、邮件、短信}(三位二进制组合)\r\n000：不进行消息处理\r\n100：只发送到界面提示。\r\n101：发送到界面和短信。\r\n110：发送到界面和邮件。\r\n111：发送到界面、邮件和短信。',
  `FUN_ID` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '事件指向页面ID',
  `TEMP_WEB` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '网页界面模板(URL)',
  `TEMP_WEB_TYPE` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT 'URL打开方式,\r\n1,弹窗打开\r\n2,新标签打开\r\n',
  `TEMP_SMS` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '短信模板',
  `TEMP_EMAIL` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '邮件模板',
  `ACTIVED` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'Y' COMMENT '是否激活，Y：激活，N：不激活',
  PRIMARY KEY (`CODE`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '系统事件类型表' ROW_FORMAT = COMPACT;

-- ----------------------------
-- Records of sys_event_type
-- ----------------------------

-- ----------------------------
-- Table structure for sys_files
-- ----------------------------
DROP TABLE IF EXISTS `sys_files`;
CREATE TABLE `sys_files`  (
  `CODE` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT 'BranchCode+5位HEX年月日5位HEX时间+3位HEX随机数',
  `REFERED` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'N' COMMENT '是否已关联',
  `REF_MODULE` varchar(5) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '关联模块：STS：安全模块相关文件',
  `REF_CODE` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '关联编号',
  `REF_TYPE` varchar(5) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '关联类别：根据实际业类各自分类定义',
  `FILE_TYPE` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '文件类型：IMG：图片; BIN：二进制文件',
  `FILE_EXT` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '文件扩展名',
  `FILE_SIZE` int(8) NOT NULL COMMENT '文件大小',
  `IS_DEFAULT` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'Y' COMMENT '关联列表中排序',
  `SOURCE_PATH` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '文件保存路径，web根目录相对路径，以\'/\'开头，如\'/attachment/upload/\'',
  `SOURCE_NAME` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '文件原始名称',
  `UPLOAD_TIME` datetime NOT NULL COMMENT '文件上传时间',
  `UPLOAD_USER` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'N' COMMENT '文件上传用户',
  PRIMARY KEY (`CODE`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '文件上传记录表' ROW_FORMAT = COMPACT;

-- ----------------------------
-- Records of sys_files
-- ----------------------------

-- ----------------------------
-- Table structure for sys_functions
