DROP TABLE IF EXISTS `sys_functions`;
CREATE TABLE `sys_functions`  (
  `ID` varchar(20) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL COMMENT '模块编号',
  `PARENT_ID` varchar(20) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT NULL COMMENT '父节点编号',
  `ORDER_NO` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '顺序号',
  `TYPE` char(1) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL COMMENT '业务类型',
  `NAME` varchar(50) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL COMMENT '模块名称',
  `DESCRIPTION` varchar(100) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL COMMENT '模块详细描述',
  `IMAGE` varchar(20) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT '0' COMMENT '图标名字',
  `OPEN_WINDOW` char(1) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL DEFAULT '0' COMMENT '是否在新窗口中打开',
  `LANG_ID` varchar(55) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT '' COMMENT '语言标记',
  `ACTIVED` tinyint(1) UNSIGNED NOT NULL DEFAULT 1 COMMENT '是否已激活',
  `PAY_FEE` char(1) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL DEFAULT 'N' COMMENT '是否涉及到付费',
  `FUNCTION_NAME` varchar(100) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT NULL COMMENT '系统调用的相关文件',
  `PARAMETERS` text CHARACTER SET utf8 COLLATE utf8_bin NOT NULL COMMENT '调用不同的头文件(可扩展，参考库存管理的该字段值)',
  `ACTIONS` text CHARACTER SET utf8 COLLATE utf8_bin NOT NULL COMMENT '权限动作列表(sys_dictionary中TableName=ACTIONS的Code列值)',
  PRIMARY KEY (`ID`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '系统功能定义表' ROW_FORMAT = COMPACT;

