-- ----------------------------
-- Table structure for sys_msg
-- ----------------------------
DROP TABLE IF EXISTS `sys_msg`;
CREATE TABLE `sys_msg`  (
  `DATA_LEVEL` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '记录位置：C为正在执行的指令，F为执行完成的指令（用于查询），用于表分区存放位置',
  `CODE` varchar(25) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '规则定义：\r\n1. 12位HEX时间+4位随机数（HEX）+5位随机数（HEX）\r\n2. 12位HEX时间+设备号后4位+5位随机数（HEX）',
  `BRANCH_CODE` varchar(5) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '分支机构编号，如果为空，则不使用发送规则。。',
  `MSG_TYPE` int(11) NOT NULL COMMENT '消息类型编号(外键SYS_MSG_TYPE->CODE)',
  `MSG_TITLE` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '消息标题',
  `MSG_CONTENT` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '发送内容',
  `NOTICE_USER` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '规则之外的消息接收者(操作员), 如: Kenny, HU',
  `NOTICE_SMS` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '规则之外的消息接收者(短信), 如: 1304954612, 15001254123',
  `NOTICE_EMAIL` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '规则之外的消息接收者(邮件), 如: 1236@qq.com, 156@163.com',
  `REF_CODE` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '消息关联编号',
  `OP_PRIORITY` int(1) NOT NULL DEFAULT 5 COMMENT '优先级[0-9] ,数字越大优先级越高\r\n…\r\n3: 低(L)\r\n4: 低于标准(B)\r\n5: 普通(N)\r\n6：高于标准(A)\r\n7: 高(H)\r\n8: 实时(R)\r\n…\r\n',
  `OP_STATUS` int(1) NOT NULL DEFAULT 1 COMMENT '处理状态：\r\n1.未处理\r\n2.正在处理\r\n3.已处理\r\n4.已取消\r\n',
  `OP_TIME` datetime NOT NULL COMMENT '处理时间',
  `OP_COUNT` int(3) NOT NULL DEFAULT 0 COMMENT '处理了的次数',
  `MAX_COUNT` int(3) NOT NULL DEFAULT 1 COMMENT '最大发送次数',
  `OP_INTERVAL` int(11) NOT NULL DEFAULT 3 COMMENT '发送间隔，单位分钟',
  `OP_START_TIME` datetime NULL DEFAULT NULL COMMENT '调度程序发送开始时间',
  `OP_END_TIME` datetime NULL DEFAULT NULL COMMENT '调度程序发送结束时间',
  `OP_RESULT` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'W' COMMENT '处理结果：\r\nW：等待发送\r\nS：发送成功\r\nF：发送失败',
  `OP_RESULT_CONTENT` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '处理结果说明',
  `COPERATOR` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '操作员',
  `CDATE` datetime NOT NULL COMMENT '操作时间',
  PRIMARY KEY (`DATA_LEVEL`, `CODE`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '系统消息记录表' ROW_FORMAT = COMPACT;

