  `ORDER_NO` int(3) NOT NULL DEFAULT 0 COMMENT '排序',
  `AUTO_OPEN` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'Y' COMMENT '是否允许登陆后自动打开',
  `ACTIVED` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'Y' COMMENT '是否激活',
