-- ----------------------------
-- Table structure for sys_trigger
-- ----------------------------
DROP TABLE IF EXISTS `sys_trigger`;
CREATE TABLE `sys_trigger`  (
  `DATA_LEVEL` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '记录位置：C为正在执行的指令，F为执行完成的指令（用于查询），用于表分区存放位置',
  `CODE` varchar(25) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '规则定义：\r\n1. 3位(触发类型)+10位HEX时间+4位随机数（HEX）+5位随机数（HEX）\r\n2. 3位(触发类型)+10位HEX时间+设备号后4位+5位随机数（HEX）\r\n',
  `TRIGTYPE` int(11) NOT NULL COMMENT '触发类型：\r\n900：设备上线\r\n901：设备下线\r\n902：事件上报\r\n903：标准事件(07000102)\r\n904：状态字串触发事件\r\n…',
  `CON_CODE` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '集中器或采集器编号',
  `DEVICE_NUM` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '设备编号:当设备不为空时，需要检测设备是否存在，如不存在，需要提交给消息中心进行提示',
  `REF_CODE` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '关联编号',
  `REF_INFO` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '关联信息(条件)\r\nTRIGTYPE = 001：事件上报字符串或事件上报文件名(长度为32个字符)\r\nTRIGTYPE = 002,003：空\r\n',
  `OP_PRIORITY` int(1) NOT NULL COMMENT '优先级[0-9] ,数字越大优先级越高\r\n…\r\n3: 低(L)\r\n4: 低于标准(B)\r\n5: 普通(N)\r\n6：高于标准(A)\r\n7: 高(H)\r\n8: 实时(R)\r\n…\r\n',
  `OP_STATUS` int(1) NOT NULL DEFAULT 1 COMMENT '处理状态：\r\n1.未处理\r\n2.正在处理\r\n3.已处理\r\n4.已取消\r\n',
  `OP_TIME` datetime NULL DEFAULT NULL COMMENT '处理时间',
  `OP_START_TIME` datetime NULL DEFAULT NULL COMMENT '调度程序发送开始时间',
  `OP_END_TIME` datetime NULL DEFAULT NULL COMMENT '调度程序发送结束时间',
  `OP_RESULT` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'W' COMMENT '处理结果：\r\nW：等待结果\r\nS：检测成功\r\nF：检测失败\r\n',
  `OP_RESULT_DATA` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '操作返回的数据',
  `COPERATOR` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '提交操作员',
  `CDATE` datetime NOT NULL COMMENT '提交时间',
  PRIMARY KEY (`DATA_LEVEL`, `CODE`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '触发器表' ROW_FORMAT = COMPACT;

