CREATE TABLE `sys_user`  (
  `CODE` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '操作员编号',
  `NAME` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '名称',
  `DESCRIPTION` varchar(150) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '描述',
  `PWD` varchar(128) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '加密之后的密码',
  `SALT` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '密码盐',
  `PWD_EXPIRY` datetime NULL DEFAULT NULL COMMENT '密码修改时间',
  `RECENT_PWD` varchar(1024) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '用户之前使用过的密码，最多储存3个，按时间顺序先后排列，用逗号分隔。',
  `COPERATOR` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '添加记录的操作员',
  `ROLE_ID` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '角色编号',
  `ROLE_ID_OTHERS` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '其他角色编号',
  `LAST_VISIT_TIME` datetime NULL DEFAULT NULL COMMENT '最后一次访问系统时间',
  `DEPT_ID` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '部门编号',
  `DEPT_ID_OTHERS` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '其他部门编号',
  `NOT_LOGIN` char(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'N' COMMENT '禁止登录标示',
  `NOT_VIEW_USER` char(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'N' COMMENT '禁止查看用户列表标示',
  `THEME` int(5) NULL DEFAULT 3 COMMENT '页面主题标示',
  `MENU_IMAGE` int(5) NULL DEFAULT 0 COMMENT '图标',
  `SHORT_CUT` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '快捷菜单编号',
  `HEAD_CUT` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '头部快捷菜单',
  `AUTO_FUN` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '登陆后自动打开的页面',
  `MENU_EXPAND` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '默认打开的菜单编号',
  `CURR_STATUS` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '1' COMMENT '当前登录状态',
  `SESSION_NO` int(11) NULL DEFAULT 0,
  `SESSION_DATE` datetime NULL DEFAULT NULL,
  `IP_LIMIT` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'N' COMMENT 'IP地址受限',
  `IP_ADDRESS` varchar(256) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '' COMMENT '受限IP地址列表',
  `AUTO_DEPT` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'N' COMMENT '自动更换部门',
  `CDATE` datetime NOT NULL COMMENT '添加记录的时间',
  `UDATE` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '变动时间',
  PRIMARY KEY (`CODE`) USING BTREE,
  INDEX `ix_DEPT_ID`(`DEPT_ID`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '系统用户表' ROW_FORMAT = COMPACT;

-- ----------------------------
-- Records of sys_user
-- ----------------------------

-- ----------------------------
-- Table structure for sys_user_details
-- ----------------------------
DROP TABLE IF EXISTS `sys_user_details`;
CREATE TABLE `sys_user_details`  (
  `CODE` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '户用编号',
  `NAME` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '用户真实姓名',
  `SEX` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '0' COMMENT '用户性别(0:男;1：女)',
  `BIRTHDAY` date NULL DEFAULT '1990-01-01' COMMENT '生日',
  `WORK_TEL` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '作工电话',
  `WORK_FAX` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '工作传真',
  `HOME_ADD` varchar(500) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '庭家住址',
  `HOME_POST` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '家庭邮编',
  `HONE_TEL` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '家庭电话',
  `MOBIL` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '手机号码',
  `EMAIL` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '邮箱',
  `OICQ` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT 'QQ号码',
  `THEME` varchar(5) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '1' COMMENT '使用主题',
  `THEME_COLOR` varchar(5) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '1' COMMENT '题主颜色',
  `ICON` varchar(150) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '/static/images/user/def.png' COMMENT '用户头像',
  `IS_MEUN_HIDE` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '0' COMMENT '否是隐藏菜单(0:显示;1:隐藏)',
  `IS_MENU_AUTO` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '0' COMMENT '是否鼠标悬浮自动展开菜单(0:否;1:是),设置为1后,菜单也将设置为隐藏(1)。',
  `IS_HEAD_HIDE` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '0' COMMENT '否是隐藏头部(0:显示;1:隐藏)',
  `IS_RIGHT_TYPE` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '0' COMMENT '右侧栏显示类型(0:浮动在上层,1:浮在左侧)',
  `IS_RIGHT_COLOR` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '0' COMMENT '右侧栏颜色(0:黑色;1:白色)',
  `IS_FATE` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '0' COMMENT '是否淡入淡出(0:不淡入淡出；1：淡入淡出)',
  `DESKTOP` varchar(1024) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '' COMMENT '桌面窗口',
  `TAB_STATUS` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '0' COMMENT 'tab页面状态\r\n0：显示多个页面\r\n1：只显示两个页面',
  PRIMARY KEY (`CODE`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '系统用户详情表' ROW_FORMAT = COMPACT;

-- ----------------------------
-- Records of sys_user_details
-- ----------------------------

