-- ----------------------------
-- Table structure for sys_verify
-- ----------------------------
DROP TABLE IF EXISTS `sys_verify`;
CREATE TABLE `sys_verify`  (
  `CODE` int(11) NOT NULL AUTO_INCREMENT COMMENT '验证编号，自动递增',
  `RECEIVE` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '验证码接收手机或邮件',
  `CDKEY` varchar(6) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '随机生成后的验证码',
  `CREATE_TIME` datetime NOT NULL COMMENT '验证码生成时间',
  `DUE_TIME` datetime NOT NULL COMMENT '验证码到期时间',
  `VALID` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'Y' COMMENT 'Y：有效；\r\nN：失效；',
  PRIMARY KEY (`CODE`) USING BTREE,
  INDEX `ix_zone_pcode`(`CDKEY`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = 'APP注册短信验证码记录表' ROW_FORMAT = COMPACT;

