-- ----------------------------
-- Table structure for sys_version
-- ----------------------------
DROP TABLE IF EXISTS `sys_version`;
CREATE TABLE `sys_version`  (
  `ID` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '版本标识：如1.1.1.140226',
  `UPGRADE_TIME` datetime NOT NULL COMMENT '更新时间',
  `NOTES` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '备注：输入变动内容及相关SQL语句，与数据库文档保持一致',
  PRIMARY KEY (`ID`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '数据库版本记录表' ROW_FORMAT = COMPACT;

