-- ----------------------------
-- Table structure for sys_zone
-- ----------------------------
DROP TABLE IF EXISTS `sys_zone`;
CREATE TABLE `sys_zone`  (
  `CODE` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '区域编号',
  `PARENT_CODE` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '' COMMENT '父节点编号',
  `DESCRIPTION` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '' COMMENT '区域名称',
  `ORDER_NO` int(3) NULL DEFAULT 0 COMMENT '排序',
  `V1` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '预留信息',
  `V2` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT 'LocationCode',
  `V3` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT 'RegionCode',
  `V4` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT 'HLoCode',
  `LEVEL_NO` smallint(1) NULL DEFAULT 0 COMMENT '层级',
  `UDATE` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '变动时间',
  PRIMARY KEY (`CODE`) USING BTREE,
  INDEX `ix_zone_pcode`(`PARENT_CODE`, `ORDER_NO`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '行政区域定义' ROW_FORMAT = COMPACT;

