CREATE TABLE `tp_request_logs`  (
  `ID` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `ROUTER` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '请求路由',
  `REQUEST` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '请求内容',
  `RESPONSE` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '回复内容',
  `REMARKS` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '备注',
