-- ----------------------------
-- Table structure for tp_server
-- ----------------------------
DROP TABLE IF EXISTS `tp_server`;
CREATE TABLE `tp_server`  (
  `CODE` int(11) NOT NULL AUTO_INCREMENT COMMENT '业务编号，自动递增\r\n',
  `DESCRIPTION` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '描述',
  `DB_TYPE` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '0' COMMENT '数据库类型\r\n0：SQL Server\r\n1：MYSQL',
  `SERVER_TYPE` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '0' COMMENT '服务类型: \r\n0: Saphir\r\n1: mmr',
  `SERVER_IP` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '第三方系统IP或域名',
  `SERVER_PORT` smallint(6) NOT NULL COMMENT '第三方系统端口',
  `SERVER_DB` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '数据库名',
  `SERVER_USER_CODE` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '第三方系统合法用户名',
  `SERVER_USER_PWD` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '第三方系统合法密码',
  `CYCLE` smallint(6) NOT NULL DEFAULT 1 COMMENT '扫描周期',
  `CYCLE_TYPE` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '4' COMMENT '周期类型（单位）：1：分，2：小时，3：天，4：月，5：年',
  `BASE_TIME` datetime NOT NULL COMMENT '基准时间',
  `NEXT_TIME` datetime NOT NULL COMMENT '下一次执行时间',
  `SEND_NOTIFY` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'N' COMMENT '\'是否已经发送通知\'',
  `IS_EXECING` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'N' COMMENT '是否正在执行',
  `ACTIVED` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'N' COMMENT '是否激活\r\nN：未激活；\r\nY：激活;\r\n只允许一条记录被激活',
  `REMARK` varchar(256) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '备注',
  `COPERATOR` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '操作员',
  `CDATE` datetime NOT NULL COMMENT '操作时间',
  PRIMARY KEY (`CODE`) USING BTREE,
  INDEX `ix_zone_pcode`(`CODE`, `SERVER_PORT`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 17 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '第三方系统连接参数' ROW_FORMAT = COMPACT;

