INSERT INTO `sys_version` VALUES ('1.1.8.200616', '2020-06-16 13:57:06', '-- 个人服务数据库表设计\r\nSET NAMES utf8mb4;\r\nSET FOREIGN_KEY_CHECKS = 0;\r\n\r\n-- ----------------------------\r\n-- Table structure for service_customer_device\r\n-- ----------------------------\r\nDROP TABLE IF EXISTS `service_customer_device`;\r\nCREATE TABLE `service_customer_device`  (\r\n  `Code` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT \'编号\',\r\n  `UserCode` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT \'用户编号\',\r\n  `RstCode` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT \'其他系统关联的Code \\r\\n1.来自vending系统bz_meter的Code\\r\\n2.来自mmr系统amr_device的Code\',\r\n  `Account` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT \'户号 \\r\\n1.来自vending系统bz_meter的RefCode\\r\\n2.来自mmr系统amr_device的Reference\',\r\n  `Name` varchar(60) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT \'1.来自vending系统bz_meter的FullName\\r\\n2.来自mmr系统amr_device的FullName\',\r\n  `DeviceNum` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT \'1.来自vending系统bz_meter的MeterNum\\r\\n2.来自mmr系统amr_device的DeviceNum\',\r\n  `Source` varchar(2) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT \'数据来源\\r\\n01:vend\\r\\n02:mmr\',\r\n  `Shown` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT \'N\' COMMENT \'Y ： 当前户号显示\\r\\nN:  当前户号不显示，需切换\',\r\n  `Address` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT \'1.来自vending系统bz_meter的Address/InstallAddress\\r\\n2.来自mmr系统amr_device的InstallAddress\',\r\n  `BillingMode` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT \'1.来自vending系统bz_meter的BillingMode\\r\\n2.来自mmr系统amr_device的BillingMode\\r\\n\\r\\n0-Prepayment - electricity\\r\\n1-Prepayment - water\\r\\n2-Prepayment - gas\\r\\n3-Prepayment - time\\r\\n4-Prepayment Currency - electricity\\r\\n5-Prepayment Currency - water\\r\\n6-Prepayment Currency - gas\\r\\n7-Prepayment Currency - time\\r\\nA-Post Payment\\r\\n\',\r\n  `Phone` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT \'\' COMMENT \'1.来自vending系统bz_meter的Phone\\r\\n2.来自mmr系统amr_device的SIMCard\',\r\n  `TotalArrears` decimal(18, 2) NOT NULL DEFAULT 0.00 COMMENT \'欠款总金额  来自售电系统\',\r\n  `Balance` decimal(18, 2) NOT NULL DEFAULT 0.00 COMMENT \'电表余额 剩余电/气/水  量\',\r\n  `BalanceRefreshTime` datetime NULL DEFAULT NULL COMMENT \'Balance  最近获取时间\',\r\n  `Others` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT \'\' COMMENT \'其他\',\r\n  `updated_at` datetime NOT NULL COMMENT \'更新时间\',\r\n  `created_at` datetime NOT NULL COMMENT \'创建时间\',\r\n  `deleted_at` datetime NULL DEFAULT NULL COMMENT \'删除时间\',\r\n  PRIMARY KEY (`Code`) USING BTREE\r\n) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;\r\n\r\nDROP TABLE IF EXISTS `service_server`;\r\nCREATE TABLE `service_server`  (\r\n  `Code` varchar(8) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT \'编号\',\r\n  `Description` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT \'描述\',\r\n  `SvcType` varchar(2) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT \'服务器类型：01 ami\',\r\n  `SvcParams` varchar(1024) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT \'参数\',\r\n  `Actived` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT \'N\' COMMENT \'是否已激活\',\r\n  `Remarks` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT \'备注\',\r\n  `Coperator` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT \'操作员\',\r\n  `updated_at` datetime NOT NULL COMMENT \'更新时间\',\r\n  `created_at` datetime NOT NULL COMMENT \'创建时间\',\r\n  `deleted_at` datetime NULL DEFAULT NULL COMMENT \'删除时间\',\r\n  PRIMARY KEY (`Code`) USING BTREE\r\n) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;\r\n\r\nSET FOREIGN_KEY_CHECKS = 1;\r\n\r\nALTER TABLE `smartpay`.`service_customer_device` \r\nADD COLUMN `TotalArrearsRefreshTime` datetime NULL DEFAULT NULL COMMENT \'TotalArrears 最近获取时间\' AFTER `TotalArrears`;\r\n');
